(112, 'Monkey Go Happy', 'monkey-go-happy', 'Unknown', 0, 4, 20, 849, '0', 'https://games.crazygames.com/en_US/monkey-go-happy/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Monkey Go Happy is a multi-level game in which you need to help the sad monkey to solve various puzzles to make it happy again!\n', '', 'monkey-go-happyy.png', 1, 1, '2022-09-11 17:04:55', '2022-09-11 17:04:55', NULL),
(113, 'Shapez', 'shapez', 'Unknown', 0, 3, 20, 625, '0', 'https://games.crazygames.com/en_US/shapez-io/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Shapez is a game developed by Tobias Springer about building factories to automate the creation and combination of shapes. At each level, you have to deliver the requested shapes. The requirements become increasingly complex. As you proceed, you can unlock upgrades to speed up your factory.', '\nDrag the left mouse button or WASD = move the camera\nRight-click = delete\nLeft-click = place building\nR = rotate building\nQ = use the pipette\nCtrl + left mouse button = select area\n', 'shapez.jpg', 1, 1, '2022-09-11 17:04:55', '2022-09-11 17:04:55', NULL),
(114, 'Keyholder', 'keyholder', 'Unknown', 0, 5, 16, 821, '0', 'https://games.crazygames.com/en_US/keyholder/index.html', 'https://crazygames.com', 'en', 'Diemorth', 28, 0, 0, 0, 0, 'Keyholder is a puzzle game where you must create a path to clear out all the locks. Move the key to the same color lock to remove it.', '', 'keyholder3.png', 1, 1, '2022-09-11 17:04:56', '2022-09-11 17:04:56', NULL),
(115, 'Chroma', 'chroma', 'Unknown', 0, 4, 17, 792, '0', 'https://games.crazygames.com/en_US/chroma/index.html', 'https://crazygames.com', 'en', 'Playtouch.', 28, 0, 0, 0, 0, 'Chroma is a coloring game to fill the grid with one color to move on to the next challenging levels.', 'Hold the left mouse button to match the colors.', 'chroma1.png', 1, 1, '2022-09-11 17:04:56', '2022-09-11 17:04:56', NULL),
(117, 'Hexa Dungeon', 'hexa-dungeon', 'Unknown', 0, 3, 12, 131, '0', 'https://games.crazygames.com/en_US/hexa-dungeon/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Hexa Dungeon is a match-3 game to connect as many tiles of the same color to attack the enemy.', 'Drag the left mouse button to connect tiles, release to attack the enemy.', 'hexa-dungeon0.png', 1, 1, '2022-09-11 17:04:58', '2022-09-11 17:04:58', NULL),
(120, 'Big Giant Games (Prison Escape Puzzle)', 'big-giant-games-prison-escape-puzzle', 'Unknown', 0, 5, 12, 45, '0', 'https://games.crazygames.com/en_US/big-giant-games/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Big Giant Games is a collection of thematic room escape games. Get some clues in the area and find your way to escape.\n', '', 'big-giant-games-prison-escape-puzzle2.png', 1, 1, '2022-09-11 17:05:00', '2022-09-11 17:05:00', NULL),
(121, 'Mahjong Pirate Plunder Journey', 'mahjong-pirate-plunder-journey', 'Unknown', 0, 4, 10, 632, '0', 'https://games.crazygames.com/en_US/mahjong-pirate-plunder-journey/index.html', 'https://crazygames.com', 'en', 'Playtouch.', 28, 0, 0, 0, 0, 'Mahjong Pirate Plunder Journey is a Mahjong game to match pairs of pirate-themed tiles.\n', '', 'mahjong-pirate-plunder-journey4.png', 1, 1, '2022-09-11 17:05:01', '2022-09-11 17:05:01', NULL),
(123, 'Mini Scientist', 'mini-scientist', 'Unknown', 0, 5, 13, 245, '0', 'https://games.crazygames.com/en_US/mini-scientist/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mini Scientist is a point and click adventure that lets you play as a little dude who wants to go to space. There\'s a little problem though - his rocket needs one more part, the engine! You\'ll need to help him go through a guarded facility to find the part he needs so he can go to his dream trip to space.', 'Tap/click on the interactable objects on the screen to use them', 'mini-scientist5.png', 1, 1, '2022-09-11 17:05:02', '2022-09-11 17:05:02', NULL),
(125, 'Grow RPG', 'grow-rpg', 'Unknown', 0, 5, 20, 571, '0', 'https://games.crazygames.com/en_US/grow-rpg/index.html', 'https://crazygames.com', 'en', 'Eyezmaze.', 28, 0, 0, 0, 0, 'Grow RPG is a cool turn based RPG game in which you must build up your kingdom and fight against the evil demon who is trying to destroy your world. Each turn you must place an object in your world such as a tree, a house, some stone, a chest or water for example. Each object has a different function and placing it in a different order will result in a certain line of the story playing out.\n', '', 'grow-rpgg.png', 1, 1, '2022-09-11 17:05:03', '2022-09-11 17:05:03', NULL),
(127, 'xor', 'xor', 'Unknown', 0, 3, 19, 561, '0', 'https://games.crazygames.com/en_US/xor/index.html', 'https://crazygames.com', 'en', 'Alex Vi.', 28, 0, 0, 0, 0, 'xor is a mind-bending, minimalist puzzle game. There are three flat figures (triangle, square and rhombus) that must be folded in a certain way to get another, more complex, figure.\n', '', 'xor.jpg', 1, 1, '2022-09-11 17:05:05', '2022-09-11 17:05:05', NULL),
(128, 'Mini Colors', 'mini-colors', 'Unknown', 0, 3, 18, 1075, '0', 'https://games.crazygames.com/en_US/mini-colors/index.html', 'https://crazygames.com', 'en', 'NoaDev.', 28, 0, 0, 0, 0, 'Switch colors to reach the goal in each level!\n', '', 'mini-colors7.png', 1, 1, '2022-09-11 17:05:05', '2022-09-11 17:05:05', NULL);
