(468, 'The Reader\'s Encounter', 'the-reader-s-encounter', 'Unknown', 0, 3, 11, 1134, '0', 'https://games.crazygames.com/en_US/the-reader-s-encounter/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'The Reader\'s Encounter is a word-arranging puzzle game. In each scenario, you need to arrange the correct word to complete the sentence.\n', '', 'the-reader-s-encounter1.png', 1, 1, '2022-09-11 17:09:21', '2022-09-11 17:09:21', NULL),
(469, '2048 Multiplayer', '2048-multiplayer', 'Unknown', 0, 3, 20, 128, '0', 'https://games.crazygames.com/en_US/2048-multiplayer-qbs/index.html', 'https://crazygames.com', 'en', '© Macte! Labs Inc.', 28, 0, 0, 0, 0, '2048 Multiplayer is the multiplayer version of the famous 2048 puzzle game!\n', '', '2048-multiplayer.jpg', 1, 1, '2022-09-11 17:09:21', '2022-09-11 17:09:21', NULL),
