(471, 'A Walk in the Park', 'a-walk-in-the-park', 'Unknown', 0, 3, 18, 1117, '0', 'https://games.crazygames.com/en_US/a-walk-in-the-park/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'A Walk in the Park is a puzzle game that sets in a park. This game combines Pacman, Sokoban, and wonderfully colorful graphics.', 'Move: arrow keysRestart level: R', 'a-walk-in-the-park.jpg', 1, 1, '2022-09-11 17:09:22', '2022-09-11 17:09:22', NULL),
(472, 'Quoridor Online', 'quoridor-online', 'Unknown', 0, 3, 13, 28, '0', 'https://games.crazygames.com/en_US/quoridor/index.html', 'https://crazygames.com', 'en', 'Motion Theory', 28, 0, 0, 0, 0, 'Quoridor Online is a tabletop strategy game and is suitable for fans of chess, checkers, and corners. The game allows you to compete with other players online, and you can also play with a computer. The more strong players you defeat, the higher your rating will be.', 'Use the left mouse button to move and use the walls.', 'quoridor-online.jpg', 1, 1, '2022-09-11 17:09:23', '2022-09-11 17:09:23', NULL),
(473, 'Hex FRVR', 'hex-frvr', 'Unknown', 0, 3, 10, 324, '0', 'https://games.crazygames.com/en_US/hex-frvr/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Hex FRVR is an easy to understand yet fun to master puzzle game. Create and destroy full lines on the screen in any of the primary three directions by dragging and dropping blocks to the screen and get amazing combo bonuses by destroying multiple lines at the same time. Made by Chris Benjaminsen.', '', 'hex-frvrc.png', 1, 1, '2022-09-11 17:09:24', '2022-09-11 17:09:24', NULL),
(474, 'Tubetris Deluxe', 'tubetris-deluxe', 'Unknown', 0, 3, 17, 1097, '0', 'https://games.crazygames.com/en_US/tubetris/index.html', 'https://crazygames.com', 'en', 'Tubetris is', 28, 0, 0, 0, 0, 'Tubetris Deluxe is a Tetris-inspired game in which the goal is to build tube lines and let them explode using a ball. The pieces of tube appear one by one, and you must stack them nicely to create a route for the ball. The ball appears occasionally and you need to place it in an opening tube to let it enter the route and destroy the tubes. The more tubes you destroy with a single ball the more points you earn. When the ball runs into an intersection you need to choose which way it goes. The ball disappears once it leaves the tubes. Good luck!', '', 'tubetris-deluxeb.png', 1, 1, '2022-09-11 17:09:24', '2022-09-11 17:09:24', NULL),
(475, 'Parking Panic', 'parking-panic', 'Unknown', 0, 3, 11, 741, '0', 'https://games.crazygames.com/en_US/parking-panic/index.html', 'https://crazygames.com', 'en', 'Parking Panic was', 28, 0, 0, 0, 0, 'Parking Panic is a puzzle game to unblock the crowded parked car and make a way for the red car to escape.', 'Drag the left mouse button to move the cars.', 'parking-panic2.png', 1, 1, '2022-09-11 17:09:25', '2022-09-11 17:09:25', NULL),
(476, 'Winners Break Rules', 'winners-break-rules', 'Unknown', 0, 4, 13, 281, '0', 'https://games.crazygames.com/en_US/winners-break-rules/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Winners Break Rules is a fun escape-room puzzle game in which you set the rules to escape and win. Over 50 rooms to escape, providing an insane challenge for even the greatest genius escape artists amongst you.\n', '', 'winners-break-rules.jpg', 1, 1, '2022-09-11 17:09:26', '2022-09-11 17:09:26', NULL),
(477, 'Flipin Squares - Match Pairs', 'flipin-squares-match-pairs', 'Unknown', 0, 4, 16, 609, '0', 'https://games.crazygames.com/en_US/flipin-squares---match-pairs/index.html', 'https://crazygames.com', 'en', 'Flipin Squares - Match Pairs was', 28, 0, 0, 0, 0, 'Flipin Squares - Match Pairs is a memory game where you find and match the characters. Complete each level and get the highest score!', 'Use the left mouse button to match the characters.', 'flipin-squares-match-pairs1.png', 1, 1, '2022-09-11 17:09:27', '2022-09-11 17:09:27', NULL);
