(533, 'Gold Mahjong FRVR', 'gold-mahjong-frvr', 'Unknown', 0, 3, 12, 980, '0', 'https://games.crazygames.com/en_US/gold-mahjong-frvr/index.html', 'https://crazygames.com', 'en', 'Chris Benjaminsen.', 28, 0, 0, 0, 0, 'Gold Mahjong FRVR is a Mahjong game with a mission to clear the gold tiles, and it comes with a daily challenge.\n', '', 'gold-mahjong-frvr7.png', 1, 1, '2022-09-11 17:10:11', '2022-09-11 17:10:11', NULL),
(534, 'Domino Pressure', 'domino-pressure', 'Unknown', 0, 5, 12, 608, '0', 'https://games.crazygames.com/en_US/domino-pressure/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Domino Pressure is a puzzle game that involves a little bit of strategy. Your goal is to push one domino block to create a domino effect and crush the tomato at the end with the final block to move on to the next level. You\'ll be given a fixed time limit when starting a game, and ending a level with blocks remaining will decrease this limit even further, but pushing all blocks in a level will add more to it. The game ends when your timer reaches zero.\n', '', 'domino-pressure2.png', 1, 1, '2022-09-11 17:10:12', '2022-09-11 17:10:12', NULL),
