(539, 'Escape From the Starship', 'escape-from-the-starship', 'Unknown', 0, 4, 13, 551, '0', 'https://games.crazygames.com/en_US/escape-from-the-starship/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Escape from the Starship is a procedurally generated puzzle game where you try to escape the ship you\'re on, while making sure that the aliens that caused the crash doesn\'t get you. You\'ll need to collect enough batteries to run your ship, and while you\'re at it, you can get a map so you know where you are and where you\'ve been. You\'ll know if there\'s an alien about to get you by listening closely to the alarms that will go off as you walk closer to a space they\'re inhabiting. Good luck on your escape - just make sure to listen to those alarms to stay alive.\n', '', 'escape-from-the-starship8.png', 1, 1, '2022-09-11 17:10:16', '2022-09-11 17:10:16', NULL),
(540, 'Please Fix The Road', 'please-fix-the-road', 'Unknown', 0, 3, 18, 273, '0', 'http://games.crazygames.com/en_US/please-fix-the-road/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Please fix the road in this new puzzle game...', '', 'please-fix-the-roadd.png', 1, 1, '2022-09-11 17:10:16', '2022-09-11 17:10:16', NULL),
(541, 'Kakuro Blend', 'kakuro-blend', 'Unknown', 0, 4, 20, 647, '0', 'https://games.crazygames.com/en_US/kakuro-blend-demo/index.html', 'https://crazygames.com', 'en', 'LankyCarrot.', 28, 0, 0, 0, 0, 'Kakuro Blend is a challenging puzzle game where you usually need to put numbers inside an empty square, but this time, play it with colors! Your goal is to fill the squares with different colors so that when they mix up, they will create the same color as the triangles. Use the color pallet on the left side to help you out. There are eight levels from 6×6 to 15×8 that you can try to solve. Good luck!', '\nLeft-click = interact with UI\nWASD / arrow keys = move around puzzle grid\n1-9 = enter colors into square\n0 = clear space\n. (period) = turns on pencil mode\n', 'kakuro-blend.jpg', 1, 1, '2022-09-11 17:10:17', '2022-09-11 17:10:17', NULL),
(542, 'Swami Shaggy', 'swami-shaggy', 'Unknown', 0, 3, 13, 639, '0', 'https://games.crazygames.com/en_US/swami-shaggy/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Swami Shaggy is a puzzle game where Shaggy plays his role as a Predictor Maga. With his crystal ball, ask him any questions.', 'Use your mouse and keyboard to ask questions.', 'swami-shaggy6.png', 1, 1, '2022-09-11 17:10:17', '2022-09-11 17:10:17', NULL),
(543, 'Tasty Tale', 'tasty-tale', 'Unknown', 0, 5, 11, 252, '0', 'https://games.crazygames.com/en_US/tasty-tale/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Tasty Tale is a game in which you have to gather all the required ingredients via a match-3 style puzzle game. Your customers are lining up and you must serve them delicious cake. Match 3 or more identical ingredients to collect them. Pay attention to the moves limit on each level and complete the task before the limit runs out. Do combos to score more points!', '', 'tasty-talee.png', 1, 1, '2022-09-11 17:10:18', '2022-09-11 17:10:18', NULL),
(545, 'Mahjong Connect 2 (Legacy)', 'mahjong-connect-2-legacy', 'Unknown', 0, 5, 13, 750, '0', 'https://games.crazygames.com/en_US/mahjong-connect-2-2/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mahjong Connect 2 is the second episode of the classical Mahjong game, Mahjong Connect.', 'Use the left mouse button to select mahjong tiles.', 'mahjong-connect-2-legacy5.png', 1, 1, '2022-09-11 17:10:20', '2022-09-11 17:10:20', NULL),
(546, '1 Pic Crossword', '1-pic-crossword', 'Unknown', 0, 5, 11, 703, '0', 'https://games.crazygames.com/en_US/one-hint-crossword/index.html', 'https://crazygames.com', 'en', 'playh5.', 28, 0, 0, 0, 0, '1 Pic Crossword is one clue crossword and one of our cool word games. Look for words based on clues you can find in a single picture. Once you found a word, a letter of that word will be another clue for the next word like in crossword puzzles.\n', '', '1-pic-crossword2.png', 1, 1, '2022-09-11 17:10:20', '2022-09-11 17:10:20', NULL),
