(691, 'Train 2048', 'train-2048', 'Unknown', 0, 4, 12, 620, '0', 'https://games.crazygames.com/en_US/train-2048/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Train 2048 is a 2048 puzzle game in which you need to merge the same numbers to keep the train moving.\n', '\nDrag the left mouse button to move the blocks, release it to make them drop.\nPress the left mouse button on the powerups and the blocks to use them\n', 'train-20486.png', 1, 1, '2022-09-11 17:12:09', '2022-09-11 17:12:09', NULL),
(692, '4 in a Row', '4-in-a-row', 'Unknown', 0, 4, 13, 402, '0', 'https://games.crazygames.com/en_US/4-in-a-row/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, '4 (Four) in a Row is a board game in which you need to be the first to get four colors in a row. The classic 4 in a row game is now available on desktop and mobile devices!\n', 'Left mouse button to drop the pieces.', '4-in-a-roww.png', 1, 1, '2022-09-11 17:12:10', '2022-09-11 17:12:10', NULL),
(693, 'Garden Bloom', 'garden-bloom', 'Unknown', 0, 4, 13, 340, '0', 'https://games.crazygames.com/en_US/garden-bloom/index.html', 'https://crazygames.com', 'en', 'Famobi.', 28, 0, 0, 0, 0, 'Garden Bloom is a classic match-3 game with a garden theme. Match as many flowers of the same color as possible (but at least 3) to score points.\n', '', 'garden-bloom4.png', 1, 1, '2022-09-11 17:12:10', '2022-09-11 17:12:10', NULL),
