(716, 'Diamond Rush 2', 'diamond-rush-2', 'Unknown', 0, 3, 16, 674, '0', 'https://games.crazygames.com/en_US/diamond-rush-2/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Diamond Rush 2 is a fun match 3 game with a space theme. Fill up the bar to finish the level. The bar will reduce if you\'re not doing anything, and if it\'s empty it will be game over!', 'Use the left mouse button to swap the jewel.', 'diamond-rush-26.png', 1, 1, '2022-09-11 17:12:28', '2022-09-11 17:12:28', NULL),
(717, 'Single Line', 'single-line', 'Unknown', 0, 3, 15, 237, '0', 'https://games.crazygames.com/en_US/single-line/index.html', 'https://crazygames.com', 'en', 'IFGD.', 28, 0, 0, 0, 0, 'Single Line is the dot to dot puzzle game in which you need to successfully draw the shape in the image without having to cross back on yourself. You can only draw a single line between each pair of dots, plan you moves carefully as once you begin drawing you could easily get stuck and have to start again.\n', 'Hold left mouse button to connect the dots.', 'single-linee.png', 1, 1, '2022-09-11 17:12:30', '2022-09-11 17:12:30', NULL),
(719, 'TriPeaks Solitaire', 'tripeaks-solitaire', 'Unknown', 0, 3, 16, 51, '0', 'https://games.crazygames.com/en_US/tri-peaks-solitaire-classic/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Tri Peaks Solitaire Classic is a classic solitaire game. Click on a card with a number which is one higher or one lower than the number on the face-up card on the bottom. Try to get rid of all the cards in this way. Enjoy!', 'Press left mouse button to play.', 'tripeaks-solitairec.png', 1, 1, '2022-09-11 17:12:31', '2022-09-11 17:12:31', NULL),
(721, 'Jewelish Blitz', 'jewelish-blitz', 'Unknown', 0, 3, 12, 183, '0', 'https://games.crazygames.com/en_US/jewelish-blitz/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Looking for a quick and challenging match-3 game? Jewelish Blitz is the answer! You only have 60 seconds to try to score as many points as possible. Match 4 or more jewels to get a special jewel that can explode when you match it. If you are quick enough at matching jewels, you can build up score multipliers. Press the light bulb button to give you a hint about where to match the jewels. Have fun!', '', 'jewelish-blitzz.png', 1, 1, '2022-09-11 17:12:32', '2022-09-11 17:12:32', NULL),
(722, 'Puzzle World', 'puzzle-world', 'Unknown', 0, 3, 11, 674, '0', 'https://games.crazygames.com/en_US/puzzle-world/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Puzzle World is the ultimate collection of puzzle games in which you can play whatever takes your fancy. There are many different types of puzzles to keep you entertained for hours. You have the opportunity to play five different puzzle games to enjoy: Sudoku, Connect, Escape, Number Link, and Plumber.\n', 'Use the left mouse button to play.', 'puzzle-worldd.png', 1, 1, '2022-09-11 17:12:32', '2022-09-11 17:12:32', NULL),
(723, '1000 Blocks', '1000-blocks', 'Unknown', 0, 3, 18, 648, '0', 'https://games.crazygames.com/en_US/1000-blocks/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, '1000 Blocks is a fun puzzle game that is inspired by Tetris. You need to destroy the required blocks by forming a complete line. Have fun!\n', '', '1000-blockss.png', 1, 1, '2022-09-11 17:12:33', '2022-09-11 17:12:33', NULL),
(724, 'The Daily Sudoku 2', 'the-daily-sudoku-2', 'Unknown', 0, 5, 19, 742, '0', 'https://games.crazygames.com/en_US/daily-sudoku-2/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'The second episode of The Daily Sudoku. Enjoy more Sudoku challenges on a daily basis.\n', 'Use the left mouse button to enter the numbers.', 'the-daily-sudoku-23.png', 1, 1, '2022-09-11 17:12:34', '2022-09-11 17:12:34', NULL),
