(767, 'Wheely 3', 'wheely-3', 'Unknown', 0, 5, 19, 892, '0', 'https://games.crazygames.com/en_US/wheely-3/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Join Wheely on his quest for a new wheel for his beloved Jolie. \nUPDATE: Wheely 4 is now available!', '', 'wheely-33.png', 1, 1, '2022-09-11 17:13:04', '2022-09-11 17:13:04', NULL),
(768, 'Wheely 6', 'wheely-6', 'Unknown', 0, 5, 13, 1032, '0', 'https://games.crazygames.com/en_US/wheely-6/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Sequel of the awesome car game Wheely 5. Help our friend Wheely to complete even more challenging levels in Wheely 6: Fairytale! Our red beetle friend enters a fairytale adventure, full of castles, lakes, and other friendly cars. There are even flying dragons and knights going around! Help him to complete all the levels by clicking in the right places. You might need some camouflage at some point! When a bandit comes out to get Wheely, you will need to find a yeti to help you. Good luck.\n', '', 'wheely-68.png', 1, 1, '2022-09-11 17:13:04', '2022-09-11 17:13:04', NULL),
(769, 'Color by Number', 'color-by-number', 'Unknown', 0, 3, 16, 692, '0', 'https://games.crazygames.com/en_US/color-by-number/index.html', 'https://crazygames.com', 'en', 'Color by Number was', 28, 0, 0, 0, 0, 'Color by Number is a free online coloring game where you paint various cute animals. Use the colors with the correct number and simply fill in the correct squares. This is a fun and relaxing art game for both kids and adults.', '\nMouse scroll to zoom-in/out\nLeft click to color\n', 'color-by-numberr.png', 1, 1, '2022-09-11 17:13:05', '2022-09-11 17:13:05', NULL),
(770, 'Lumeno', 'lumeno', 'Unknown', 0, 3, 16, 1078, '0', 'https://games.crazygames.com/en_US/lumeno/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Lumeno is a colored-balls connecting game to get as many scores as possible within the given move counts.\n', '', 'lumeno8.png', 1, 1, '2022-09-11 17:13:05', '2022-09-11 17:13:05', NULL),
(771, 'Word Sauce', 'word-sauce', 'Unknown', 0, 3, 12, 350, '0', 'https://games.crazygames.com/en_US/word-sauce/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Word Sauce is a word-search game to find all the hidden words and complete different missions around the house.\n', '', 'word-sauce.jpeg', 1, 1, '2022-09-11 17:13:06', '2022-09-11 17:13:06', NULL),
(772, 'Snail Bob 5', 'snail-bob-5', 'Unknown', 0, 5, 18, 1088, '0', 'https://games.crazygames.com/en_US/snailbob-5/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Snail Bob 5 is the latest instalment of the awesome Snail Bob platform series. In this episode, Bob has his eyes set on a beautiful female singer snail and he wants to meet her at her latest concert! You must help Bob meet his true love and pass through a dangerous forest full of obstacles and challenges.\n', '\nLeft click to interact\nSpace bar to hide in the shell\n1 to turn around\n2 to speed up\n', 'snail-bob-5b.png', 1, 1, '2022-09-11 17:13:07', '2022-09-11 17:13:07', NULL),
(773, 'Mahjong Dark Dimensions', 'mahjong-dark-dimensions', 'Unknown', 0, 4, 17, 533, '0', 'https://games.crazygames.com/en_US/mahjong-dark-dimensions/index.html', 'https://crazygames.com', 'en', 'Arkadium.', 28, 0, 0, 0, 0, 'Mahjong Dark Dimensions is a free 3D Mahjong game. The goal of the game is to remove all tiles. Click two tiles to match them. You cannot click tiles that are blocked by other tiles on their left and right sides. ', '\nPress left mouse button to choose a tile\nPress AD or left/right arrow or drag the left mouse button to rotate\n', 'mahjong-dark-dimensions9.png', 1, 1, '2022-09-11 17:13:07', '2022-09-11 17:13:07', NULL),
