(918, 'Solitaire Quest', 'solitaire-quest', 'Unknown', 0, 4, 17, 1096, '0', 'https://games.crazygames.com/en_US/solitaire-quest/index.html', 'https://crazygames.com', 'en', 'Softgames-Azerion', 28, 0, 0, 0, 0, 'Solitaire Quest is a fun solitaire card game. Try to bring each level to a good end. Good luck! ', 'Use the left mouse button to click on the card.', 'solitaire-quest6.png', 1, 1, '2022-09-11 17:14:52', '2022-09-11 17:14:52', NULL),
(919, 'Caveman Adventure', 'caveman-adventure', 'Unknown', 0, 3, 13, 200, '0', 'https://games.crazygames.com/en_US/caveman-adventure/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Caveman Adventure is a fun and adorable puzzle game in which you must guide our caveman through a series of levels and obstacles to reach his cave! The levels start off quite easy and you must simply click on the caveman to make him walk – he will always walk forward – you have to place certain objects to alter his course or to make him avoid obstacles.', 'Use the left mouse button to place items and make the caveman moves.', 'caveman-adventuree.png', 1, 1, '2022-09-11 17:14:53', '2022-09-11 17:14:53', NULL),
