(924, 'Kris Mahjong: Remastered', 'kris-mahjong-remastered', 'Unknown', 0, 4, 17, 395, '0', 'https://games.crazygames.com/en_US/kris-mahjong-remastered/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Kris Mahjong: Remastered is the remastered version of Kris Mahjong. Now you can enjoy the game with more beautiful graphics!\n', '', 'kris-mahjong-remastered.jpeg', 1, 1, '2022-09-11 17:14:57', '2022-09-11 17:14:57', NULL),
(925, 'Lines', 'lines', 'Unknown', 0, 3, 15, 494, '0', 'https://games.crazygames.com/en_US/lines/index.html', 'https://crazygames.com', 'en', 'Gamious.', 28, 0, 0, 0, 0, 'Lines is a puzzle game in which you need to place the dots correctly to create the longest lines.\n', '', 'lines.jpeg', 1, 1, '2022-09-11 17:14:58', '2022-09-11 17:14:58', NULL),
(926, 'Mahjongg Dimensions', 'mahjongg-dimensions', 'Unknown', 0, 3, 13, 421, '0', 'https://games.crazygames.com/en_US/mahjongg-dimensions/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mahjongg Dimensions is a 3D Mahjongg game where you have to complete as many tile-matching puzzles as possible before the time runs out. The game features standard Mahjongg patterns, but you must rotate the 3D block of tiles to find tile matches. Prepare to take your Mahjongg skills to the third dimension!\n', '\nChoose tiles: click the left mouse button\nRotate: drag the left mouse button or AD or left and right arrows\n', 'mahjongg-dimensions.jpeg', 1, 1, '2022-09-11 17:14:59', '2022-09-11 17:14:59', NULL),
(927, 'Farm Puzzle', 'farm-puzzle', 'Unknown', 0, 3, 18, 583, '0', 'https://games.crazygames.com/en_US/farm-puzzle/index.html', 'https://crazygames.com', 'en', 'Inlogic Software.', 28, 0, 0, 0, 0, 'Farm Puzzle is a jigsaw puzzle game with a farm-animals theme, that is suitable for kids and younger audiences.\n', '', 'farm-puzzle2.png', 1, 1, '2022-09-11 17:15:00', '2022-09-11 17:15:00', NULL),
(928, 'Dibbles: Pro Pack', 'dibbles-pro-pack', 'Unknown', 0, 3, 16, 1086, '0', 'https://games.crazygames.com/en_US/dibbles/index.html', 'https://crazygames.com', 'en', 'Dibbles was', 28, 0, 0, 0, 0, 'Dibbles: Pro Pack is a logic game with the objective to safely guide the king to the finish point. Give the right orders to the Dibbles in order to make the king arrive safely.', 'Use the left mouse button to place the instructed stone on the ground.', 'dibbles-pro-pack8.png', 1, 1, '2022-09-11 17:15:00', '2022-09-11 17:15:00', NULL),
(929, 'Heart Box', 'heart-box', 'Unknown', 0, 4, 16, 1129, '0', 'https://games.crazygames.com/en_US/heart-box/index.html', 'https://crazygames.com', 'en', 'Konstantin Radchenko.', 28, 0, 0, 0, 0, 'Heart Box is a fun physics based puzzle game that takes inspiration from the hugely popular Cut the Rope title. In this title you must destroy objects and make use of physics to move the black box into the red container at the end of each level. Look at how the different objects, platforms and obstacles are positioned and consider how destroying one could affect the path of the box.\n', '', 'heart-boxx.png', 1, 1, '2022-09-11 17:15:01', '2022-09-11 17:15:01', NULL),
(930, 'Word Twister', 'word-twister', 'Unknown', 0, 4, 14, 60, '0', 'https://games.crazygames.com/en_US/word-twister/index.html', 'https://crazygames.com', 'en', 'Microsoft.', 28, 0, 0, 0, 0, 'Word Twister is one of Microsoft Ultimate Word Games in which you need to rearrange letter tiles to find the hidden words.', 'Drag left mouse button', 'word-twister.jpeg', 1, 1, '2022-09-11 17:15:01', '2022-09-11 17:15:01', NULL),
(931, 'Mancala', 'mancala', 'Unknown', 0, 4, 15, 621, '0', 'https://games.crazygames.com/en_US/mancala-3d/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mancala is a 3D strategy board game where you have to capture more stones than your opponent. Each player has 6 holes with four stones in each. You can player Mancala solo or against a friend in local multiplayer. Move the stones and see who wins the most!\n', '', 'mancala7.png', 1, 1, '2022-09-11 17:15:02', '2022-09-11 17:15:02', NULL),
(932, 'Find a Pair Animals', 'find-a-pair-animals', 'Unknown', 0, 5, 18, 714, '0', 'https://games.crazygames.com/en_US/find-a-pair-animals/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Find a Pair Animals is a memory game where you match pairs of animals. Click the face-down cards to flip them face-up, two at a time. If they match, they\'ll be removed from the card selection. More cards are added as you progress!', 'Tap/click the card to flip them over and reveal the animal on the card.', 'find-a-pair-animals4.png', 1, 1, '2022-09-11 17:15:02', '2022-09-11 17:15:02', NULL),
(933, 'OddBods: Sticky Tacky', 'oddbods-sticky-tacky', 'Unknown', 0, 5, 16, 634, '0', 'https://games.crazygames.com/en_US/oddbods-sticky-tacky/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Sticky Tacky is a puzzle game from the OddBods series where you make the cute characters and objects stick and stack together successfully.', 'Drag the left mouse button to move the objects', 'oddbods-sticky-tacky7.png', 1, 1, '2022-09-11 17:15:03', '2022-09-11 17:15:03', NULL),
