(1030, 'The Arabian Nights: Sinbad the Voyager', 'the-arabian-nights-sinbad-the-voyager', 'Unknown', 0, 3, 13, 319, '0', 'https://games.crazygames.com/en_US/arabian-night-1001/index.html', 'https://crazygames.com', 'en', 'Human Soft, Inc.', 28, 0, 0, 0, 0, 'Arabian Night 1001 is a hidden object game that is based on the original book of Arabian Nights.', 'Left mouse button', 'the-arabian-nights-sinbad-the-voyager5.png', 1, 1, '2022-09-11 17:16:15', '2022-09-11 17:16:15', NULL),
(1031, 'Mahjong Jong', 'mahjong-jong', 'Unknown', 0, 4, 12, 966, '0', 'https://games.crazygames.com/en_US/mahjong-jong/index.html', 'https://crazygames.com', 'en', '', 28, 0, 0, 0, 0, 'Mahjong Jong is a relaxing game to test your mahjong skills (i.e. memory, attention to detail, and quick matching skills). This is a new HTML5 mahjong game with endless puzzles. Have some fun!', 'Use the left mouse button to select the mahjong tiles.', 'mahjong-jong4.png', 1, 1, '2022-09-11 17:16:16', '2022-09-11 17:16:16', NULL),
(1033, 'Trollface Quest: Video Memes and TV Shows', 'trollface-quest-video-memes-and-tv-shows', 'Unknown', 0, 3, 10, 518, '0', 'https://games.crazygames.com/en_US/trollface-quest-video-memes-and-tv-shows/index.html', 'https://crazygames.com', 'en', 'PPLLAAYY.', 28, 0, 0, 0, 0, 'Trollface Quest: Video Memes and TV Shows is a double edition of the famous Trollface Quest game series. In this game, you can enjoy both video memes and TV shows episodes at the same time! The goal in the game is to prevent yourself from getting trolled by clicking the correct things or objects in the game. Can you solve all the levels? Have fun!\n', '', 'trollface-quest-video-memes-and-tv-shows3.png', 1, 1, '2022-09-11 17:16:18', '2022-09-11 17:16:18', NULL),
