(1617, 'Magic Lines', 'magic-lines', 'html5', 0, 3, 14, 644, '0', 'https://html5.gamedistribution.com/6140bc25d838456c998b28bcf1fcf02f/?gd_sdk_referrer_url=https://gamedistribution.com/games/magic-lines', 'https://gamedistribution.com', 'en', 'BestGamesFreePlay.com', 28, 0, 0, 0, 0, 'Making lines (vertical, horizontal, or diagonal) of 5 or more balls with the same color to destroy them. Don\'t let the balls fully fill out the board.\n\nUse the special items (Undo, Hammer, and Bomb) wisely to overcome the hard situations.\nClick the Gift button to visit the Lucky Wheel, where you can spin to get bonus items.\nVisit the game everyday to earn daily rewards (an extra spin).\n\nTry to reach high scores and raise your name on the leaderboard. Enjoy!', 'Tap a ball to select it.\nTap an empty cell to move the ball to it.\nMaking lines of 5 or more balls with the same color to destroy them.\nUse Hammer or Bomb item to destroy balls.', 'magic-lines.jpeg', 1, 1, '2022-09-11 22:22:20', '2022-09-11 22:22:20', NULL),
(1618, 'Pic Pie Puzzles', 'pic-pie-puzzles', 'html5', 0, 3, 19, 939, '0', 'https://html5.gamedistribution.com/59946d2488a14e47ad8ebf28b40698c0/?gd_sdk_referrer_url=https://gamedistribution.com/games/pic-pie-puzzles', 'https://gamedistribution.com', 'en', 'Lof Games', 28, 0, 0, 0, 0, 'Pic Pie Puzzles is a photo puzzle game. In a level you will find some pie shaped pieces of a round photo. You need to swipe mouse or finger on 2 adjacent pie pieces to swap them. Repeat this as required until you form the correct photo.', 'Use mouse or touch pad to play this game.', 'pic-pie-puzzles.jpeg', 1, 1, '2022-09-11 22:22:22', '2022-09-11 22:22:22', NULL),
(1619, 'A Mole in a Hole', 'a-mole-in-a-hole', 'html5', 0, 3, 12, 692, '1', 'https://html5.gamedistribution.com/b26a37b4315442f58f04106c3800121a/?gd_sdk_referrer_url=https://gamedistribution.com/games/a-mole-in-a-hole', 'https://gamedistribution.com', 'en', 'Catom Games', 28, 0, 0, 0, 0, 'Help a forgetful mole to find his way home!\nLook at the map and choose the right path!\nGo home to your wife before dinner is ready! She\'is waiting for you!\n', 'Touch the button on the screen to choose the mole path', 'a-mole-in-a-hole.jpeg', 1, 1, '2022-09-11 22:22:25', '2022-09-11 22:22:25', NULL);
